package handshake

import (
	"crypto/x509"
	"encoding/hex"
	"testing"
)

func TestCertificateDecode(t *testing.T) {

	// https://tls13.xargs.org/#server-certificate
	certificateHex := ""
	bytes, _ := hex.DecodeString(certificateHex)

	// Decode as Certificate record
	_, decoded := DecodeHandShake(bytes)

	// parse and assert decoded certificate
	cert, err := x509.ParseCertificate(decoded.Certificate.certificateList[0].certData)
	if err != nil {
		t.Fatal("Certificateion perce failed")
	}

	if cert.Issuer.String() != "O=Example CA,C=US" {
		t.Fatalf("Unexpected issuer %v", cert.Issuer.String())
	}
}
